package sec02.exam03;

public class DenyLogigOperatorExample {
	//값을 표시할 때 !는 not이라고 생각
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean play = true;
		System.out.println(play);
		
		play = !play;
		System.out.println(play);
		
		play = !play;
		System.out.println(play);
	}

}
